mapper.select:
SELECT
	STAT.WORK_ID,
	STAT.WORK_STAT_CD,
	STAT.EQP_ID,
	STAT.RSN_CD,
	JOB.OUT_PORT_ID,
	JOB.JOB_SEQ_ID,WN_WIP_STAT
	JOB.OBJ_ID
FROM WN_WORK_STAT STAT, WN_WORK_JOB JOB
WHERE 1=1
  AND STAT.WORK_ID = JOB.WORK_ID
  AND STAT.WORK_STAT_CD = 'Active'
  AND JOB.JOB_STAT_CD = 'Active'
  AND STAT.EQP_ID = #{eqpId}
  AND JOB.LOT_ID = #{lotId}
AND OUT_PORT_ID = #{outPortId}
AND IN_PORT_ID = #{inPortId}
AND WORK_STAT_CD = #{useStatCd}
AND USE_STAT_CD = #{siteId}
ORDER BY JOB_SEQ_ID

mapper.update:
UPDATE WN_WORK_STAT

	EQP_ID = #{eqpId},
	WORK_STAT_CD = #{workStatCd},
	WORK_START_TM = SYSTIMESTAMP,
	WORK_QTY = #{workQty},
	FNL_TASK_NM = #{fnlTaskNm},
	BATCH_YN = #{batchYn},
	INLINE_YN = #{inlineYn},
	EVNT_NM = #{evntNm},
	PREV_EVNT_NM = EVNT_NM,
	CSTM_EVNT_NM = #{cstmEvntNm},
	PREV_CSTM_EVNT_NM = CSTM_EVNT_NM,
	USE_STAT_CD = #{useStatCd},
	RSN_CD = #{rsnCd},
	TRNS_CM = #{trnsCm},
	MDFY_USER_ID = #{mdfyUserId},
	MDFY_DT = SYSTIMESTAMP,
	FNL_EVNT_DT = SYSTIMESTAMP,
	TID = #{tid},
	EQP_INLINE_ID = #{eqpInlineId},
	INLINE_STAT_CD = #{inlineStatCd},
	DSP_WORK_ID = #{dspWorkId},			


	AND OBJ_ID = #{pObjId}
	AND SITE_ID = #{pSiteId}
	AND WORK_ID = #{pWorkId}
	AND EQP_ID = #{pEqpId}
	AND WORK_STAT_CD = #{pWorkStatCd}
	AND USE_STAT_CD = #{pUseStatCd}


	SELECT OBJ_ID AS objId
	  FROM WN_WORK_STAT

		AND OBJ_ID = #{pObjId}
		AND SITE_ID = #{pSiteId}
		AND WORK_ID = #{pWorkId}
		AND EQP_ID = #{pEqpId}
		AND WORK_STAT_CD = #{pWorkStatCd}
		AND USE_STAT_CD = #{pUseStatCd}

mapper.insert:
INSERT INTO WN_WORK_STAT (
	 OBJ_ID
	,SITE_ID
	,WORK_ID
	,EQP_ID
	,WORK_STAT_CD
	,WORK_START_TM
	,WORK_QTY
	,FNL_TASK_NM
	,BATCH_YN
	,INLINE_YN
	,EVNT_NM
	,PREV_EVNT_NM
	,CSTM_EVNT_NM
	,PREV_CSTM_EVNT_NM
	,USE_STAT_CD
	,RSN_CD
	,TRNS_CM
	,CRT_USER_ID
	,CRT_DT
	,MDFY_USER_ID
	,MDFY_DT
	,FNL_EVNT_DT
	,TID
	,EQP_INLINE_ID
	,INLINE_STAT_CD
	,DSP_WORK_ID
)
VALUES (
	 #{objId}
	,#{siteId}
	,#{workId}
	,#{eqpId}
	,#{workStatCd}
	,#{workStartTm}
	,#{workQty}
	,#{fnlTaskNm}
	,#{batchYn}
	,#{inlineYn}
	,#{evntNm}
	,#{prevEvntNm}
	,#{cstmEvntNm}
	,#{prevCstmEvntNm}
	,#{useStatCd}
	,#{rsnCd}
	,#{trnsCm}
	,#{crtUserId}
	,SYSTIMESTAMP
	,#{mdfyUserId}
	,SYSTIMESTAMP
	,SYSTIMESTAMP
	,#{tid}
	,#{eqpInlineId}
	,#{inlineStatCd}
	,#{dspWorkId}

)

	SELECT TO_CHAR(SYSTIMESTAMP,'YYYYMMDDHH24MISSFF6') || '-' || SYS_GUID() as objId FROM DUAL
